// import React from "react";
// import React, { useEffect } from 'react'
import React, { useState, useEffect } from "react";

// import MUIDataTable from "mui-datatables";
import API from '../../api';
// components
import PageTitle from "../../components/PageTitle/PageTitle";

import {
  Grid,
  // CircularProgress,
  // Typography,
  Button,
  // Tabs,
  // Tab,
  TextField,
  // Fade,
} from "@material-ui/core";

// import { useNavigate } from "react-router-dom";
import Select from 'react-select';


export default function CreateIncome() {



  const [incomeId, setIncomeId] = useState(null)
  const [description, setDescription] = useState(null)
  const [amount, setAmount] = useState(null)
  const [incomeCategory, setIncomeCategory] = useState([])
  const createOneIncome = async (e) => {
    e.preventDefault();

    const formData = new FormData()

    formData.append('incomeId', incomeId)
    formData.append('incomeCategory', incomeCategory)
    formData.append('description', description)
    formData.append('amount', amount)



  const res1 = await API.post(`/income`, formData)
console.log(res1)
  // navigate("/")
}
// this.state = {
//   list: [],
//   chosenValue: '',
// }
// useEffect(() => {
//   fetchExpenses()
// }, [])

onDropdownChange = (e) => {
  this.setIncomeCategory({ incomeCategory: e.target.value })
}
  return (
    <>
      <PageTitle title="Register New Income" />
      <Grid container spacing={4}>
        <Grid item xs={12}>
        <TextField
                id="incomeId"
                margin="normal"
                placeholder="Income ID"
                type="text"
                fullWidth
                value={incomeId} 
                onChange={(event)=>{
                  setIncomeId(event.target.value)

                }}/>




        <TextField
                id="description"
                margin="normal"
                placeholder="Description"
                type="text"
                fullWidth
                value={description} 
                onChange={(event)=>{
                  setDescription(event.target.value)
                }}/>

        <TextField
                id="amount"
                margin="normal"
                placeholder="Amount"
                type="text"
                fullWidth
                value={amount} 
                onChange={(event)=>{
                  setAmount(event.target.value)
                }}/> 

          <Select
           id="incomeCategory"
           margin="normal" 
          options={incomeCategory.map(category => ({
            label: category.incomeCategoryType,
            value: category.id
          }))} 
          onChange={this.onDropdownChange}

          />
          <br/>  

                  <Button
                   variant="contained"
                    color="primary"
                    size="large"
                    onClick={createOneIncome}
                  >
                    Submit
                  </Button>

        </Grid>

      </Grid>
    </>
  );
}
