import { useMemo } from "react";
// import React, { useEffect } from 'react'
import React, { useState, useEffect } from "react";

// import MUIDataTable from "mui-datatables";
import API from '../../api';
// components
import PageTitle from "../../components/PageTitle/PageTitle";

import {
  Grid,
  // CircularProgress,
  // Typography,
  Button,
  // Tabs,
  // Tab,
  TextField,
  MenuItem,
  Select
  // Fade,
} from "@material-ui/core";

// import { useNavigate } from "react-router-dom";
// import Select from 'react-select';
// import { SelectFetch } from 'react-select-fetch';


export default function CreateIncome() {

  const [incomeId, setIncomeId] = useState("")
  const [description, setDescription] = useState("")
  const [amount, setAmount] = useState(0)
  const [incomeCategories, setIncomeCategories] = useState([])
  const [incomeCategory, setIncomeCategory] = useState(null);

  const createOneIncome = async (e) => {
    e.preventDefault();

    const formData = new FormData()

    formData.append('incomeId', incomeId)
    formData.append('incomeCategory', incomeCategories)
    formData.append('description', description)
    formData.append('amount', amount)



    const res1 = await API.post(`/income`, formData)

  }
  useEffect(() => {
    fetchIncome()
  }, [])
  const fetchIncome = useMemo(async () => {
    await API.get(`/income_category`).then(({ data }) => {
      setIncomeCategories(data)
    })
  });

  return (
    <>
      <PageTitle title="Register New Income" />
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <TextField
            id="incomeId"
            placeholder="Income ID"
            type="text"
            fullWidth
            value={incomeId}
            onChange={(event) => {
              // setIncomeId(event.target.value)

            }} />




          <TextField
            id="description"
            placeholder="Description"
            type="text"
            fullWidth
            value={description}
            onChange={(event) => {
              // setDescription(event.target.value)
            }} />

          <TextField
            id="amount"
            placeholder="Amount"
            type="text"
            fullWidth
            value={amount}
            onChange={(event) => {
              // setAmount(event.target.value)
            }} />

          <Select
            id="incomeCategory"
            value={incomeCategory}
            onChange={(e) => {
              console.log({ e })
              // setIncomeCategory(value);
            }}

          >
            <MenuItem value={null}>Select Category</MenuItem>
            {incomeCategories.map(category => <MenuItem value={category.id} key={category.id}>
              {category.incomeCategoryType}
            </MenuItem>)}
          </Select>

          <br />

          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={createOneIncome}
          >
            Submit
          </Button>

        </Grid>

      </Grid>
    </>
  );
}
